// q_theta(alpha): theta = pair, alpha = pairp
// Cf., p. 201 of Dolgachev
function QuadraticForm(pair, pairp)
  epsilon, eta := Explode(pair);
  epsilonp, etap := Explode(pairp);
  return DotProduct([epsilon, etap]) + DotProduct([eta, etap]) + DotProduct([etap, etap]);
end function;

Steiners_unique := [];
Steiners := AssociativeArray();
for alpha in char_pairs do
  S := [];
  for theta in char_pairs do
    if QuadraticForm(theta, alpha) eq 0 then
      Append(~S, theta);
    end if;
  end for;
  // only new Steiner systems
  new_bool := true;
  for el in Steiners do
    if Set(S) eq Set(el) then
      new_bool := false;
    end if;
  end for;
  if new_bool then
    Append(~Steiners_unique, S);
  end if;
  Steiners[alpha] := S;
end for;


